export const getYearsList = () => {
  const years = [];
  for (let year = 2024; year >= 1999; year--) {
    years.push(year);
  }
  return years;
};

export const getSongsForYear = (year: number) => {
  return allMockSongs.map((song, index) => {
    const currentYearRank = song.history.find(h => h.year === year)?.rank || index + 1;
    const previousYearRank = song.history.find(h => h.year === year - 1)?.rank;
    
    // Calculate rank change (negative means improved, positive means dropped)
    let rankChange = null;
    if (previousYearRank !== undefined) {
      rankChange = previousYearRank - currentYearRank;
    }
    
    return {
      ...song,
      rank: index + 1,
      yearRank: currentYearRank,
      rankChange
    };
  }).sort((a, b) => a.yearRank - b.yearRank);
};

export const getArtistSongs = (artistId: string) => {
  return allMockSongs.filter(song => song.artistId === artistId);
};

export const getSongById = (id: string) => {
  return allMockSongs.find(song => song.id === id);
};

// Get all artists with calculated song counts
export const getAllArtistsWithSongCount = (): Artist[] => {
  return mockArtists.map(artist => {
    const songCount = allMockSongs.filter(song => song.artistId === artist.id).length;
    return { ...artist, songCount };
  });
};

export const getArtistById = (id: string) => {
  const artist = mockArtists.find(artist => artist.id === id);
  if (artist) {
    // Calculate songCount dynamically
    const songCount = allMockSongs.filter(song => song.artistId === id).length;
    return { ...artist, songCount };
  }
  return artist;
};
